package maestro.control;import maestro.form.*;import maestro.entity.*;import db.*;import java.util.Collection;import java.sql.SQLException;public class gstcentrocosto extends GstTabla {	public gstcentrocosto() {		db = new beanConnector();		this.classEntidad = centrocosto.class;	}	public gstcentrocosto(beanConnector db) {		this.db = db;		this.classEntidad = centrocosto.class;	}	public Collection getlistacentrocosto() {		String consulta = " SELECT * FROM centrocosto order by cccod desc";		return getLista(consulta);	}	public Collection getlistacentrocosto(String start, String param) {		start = start == null ? "0" : start;		param = param == null ? "" : param;		String consulta = " SELECT * FROM centrocosto where " + getTextoBusquedaResumen(param) + " order by cccod asc " + "  limit " + this.maxfilas + " offset  " + (Integer.parseInt(start) * this.maxfilas);		return getLista(consulta);	}	public int getTotalPaginaslistacentrocosto(String param) {		param = param == null ? "" : param;		String consulta = " SELECT count(1) FROM centrocosto where  " + getTextoBusquedaResumen(param);		return getPaginas(consulta);	}	public boolean crearcentrocosto(String cccod,String ccdesc) throws SQLException {		String insert = "INSERT INTO centrocosto (cccod, ccdesc) VALUES (" + (cccod == null ? "NULL" : "'" + cccod + "',")  + (ccdesc == null ? "NULL" : "'" + ccdesc + "'") + ")";		int resp = db.executeUpdate(insert);		return resp == 0 ? false : true;	}	public centrocosto getcentrocosto(String cccod) {		String cad = "SELECT * FROM centrocosto WHERE cccod='" + cccod + "'";		return (centrocosto) getEntidad(cad);	}	public boolean updatecentrocosto(centrocostoForm entity) throws SQLException {		String cad = " update centrocosto set  " + " ccdesc = " + (entity.getccdesc() == null ? "NULL" : "'" + entity.getccdesc() + "'") + " where cccod = " + entity.getcccod();		int resp = db.executeUpdate(cad);		return resp == 0 ? false : true;	}	public boolean updatecentrocosto(centrocosto entity) throws SQLException {		String cad = " update centrocosto set  " + " ccdesc = " + (entity.getccdesc() == null ? "NULL" : "'" + entity.getccdesc() + "'") + " where cccod = " + entity.getcccod();		int resp = db.executeUpdate(cad);		return resp == 0 ? false : true;	}		public boolean eliminar(String cccod) throws SQLException {		String elim = " delete from centrocosto where cccod = '" + cccod + "'";		int r = db.executeUpdate(elim);		return r == 0 ? false : true;	}}