package pedido.control;import pedido.form.*;import pedido.entity.*;import db.*;import java.util.Collection;import java.sql.SQLException;public class gstreferencia_despacho_serial extends GstTabla {	public gstreferencia_despacho_serial() {		db = new beanConnector();		this.classEntidad = referencia_despacho_serial.class;	}	public gstreferencia_despacho_serial(beanConnector db) {		this.db = db;		this.classEntidad = referencia_despacho_serial.class;	}	public Collection getlistareferencia_despacho_serial() {		String consulta = " SELECT * FROM referencia_despacho_serial";		return getLista(consulta);	}	public Collection getlistareferencia_despacho_serial(String refdsreferencia_despacho) {		String consulta = " SELECT * FROM referencia_despacho_serial where refdsreferencia_despacho=" + refdsreferencia_despacho;		return getLista(consulta);	}	public boolean crearreferencia_despacho_serial(String refdsreferencia_despacho, String refdsserial) throws SQLException {		String insert = "INSERT INTO referencia_despacho_serial (refdsreferencia_despacho,refdsserial) VALUES (" + (refdsreferencia_despacho == null ? "NULL" : "'" + refdsreferencia_despacho + "'") + ","				+ (refdsserial == null ? "NULL" : "'" + refdsserial + "'") 				+ ")";		int resp = db.executeUpdate(insert);		return resp == 0 ? false : true;	}		public referencia_despacho_serial getreferencia_despacho_serial(String refdscodsx) {		String cad = "SELECT * FROM referencia_despacho_serial WHERE refdscodsx='" + refdscodsx + "'";		return (referencia_despacho_serial) getEntidad(cad);	}	public boolean eliminar(String refdscodsx) throws SQLException {		String elim = " delete from referencia_despacho_serial where refdscodsx = '" + refdscodsx + "'";		int r = db.executeUpdate(elim);		return r == 0 ? false : true;	}}